<script setup lang="ts">
const emit = defineEmits(['onPickDesk'])
const props = defineProps<{
  selectedHall: string | null
  date: string;
  from: string;
  to: string;
  tables: Table[];
  bookings: Booking[];
  capacity: number
}>()

const checkReserve = (tableId: string) => {
  if (!props.tables || !props.bookings) {
    return false
  }
  return props.bookings.some(booking => {
    if (booking.tableId !== tableId) return false;
    if (booking.date !== props.date) return false;
    if (booking.status === 'cancelled') return false;

    const bookingFrom = new Date(`1970-01-01T${booking.from}:00`);
    const bookingTo = new Date(`1970-01-01T${booking.to}:00`);
    const requestedFrom = new Date(`1970-01-01T${props.from}:00`);
    const requestedTo = new Date(`1970-01-01T${props.to}:00`);

    return requestedFrom < bookingTo && requestedTo > bookingFrom;
  });

}

const checkCapacity = (tableId: string) => {
  if (props.capacity === 0) return false

  const selectedTable = props.tables.find(table => table._id === tableId);
  if (!selectedTable) return false;

  return selectedTable.capacity < props.capacity
}
</script>

